// On window load...
function onWindowLoad() {
    // If you're on google meet
    -1 != document.title.toString().indexOf("Meet - ") && chrome.runtime.connect({
        name: "gmeet"
    }).postMessage({
        // Tell Securly exactly what meet you're in!
        action: "getGoogleMeetUrl",
        url: window.location.href
    })
}

// Weird load timeout thing again
function callWindowLoadWithTimeOut() {
    setTimeout(onWindowLoad, 2e3)
}

// Attach the weird load timeout thing
window.addEventListener ? window.addEventListener("load", callWindowLoadWithTimeOut, !1) : 
window.attachEvent && window.attachEvent("onload", callWindowLoadWithTimeOut);